import time
class Solution:
    # Time limit exceeded Solution, Complexity O(n*k)
    def characterReplacement(self, s: str, k: int) -> int:
        n = len(s)
        if n < 2:
            return n
        prev = s[0]
        max_l = 0
        for i in range(n):
            # print(i)
            if prev != s[i] or i == 0:
                j = i
                rem_k = k
                c = 0     
                while j < n and rem_k >= 0:
                    if j >= 0:
                        if s[j] != s[i]:
                            rem_k -= 1
                        if rem_k >= 0:
                            c += 1
                    j += 1
                j = i-1
                while j >= 0 and rem_k >= 0:
                    if s[j] != s[i]:
                            rem_k -= 1
                    if rem_k >= 0:
                        c += 1
                    j -= 1

                if c > max_l:
                    max_l = c
                prev = s[i]
        return max_l

        # Complexity n Solution

