import time
class Solution:
    # Time limit exceeded Solution, Complexity O(n*k)
    def characterReplacement(self, s: str, k: int) -> int:
        n = len(s)
        if n < 2:
            return n
        prev = s[0]
        max_l = 0
        for i in range(n):
            # print(i)
            if prev != s[i] or i == 0:
                j = i
                rem_k = k
                c = 0     
                while j < n and rem_k >= 0:
                    if j >= 0:
                        if s[j] != s[i]:
                            rem_k -= 1
                        if rem_k >= 0:
                            c += 1
                    j += 1
                j = i-1
                while j >= 0 and rem_k >= 0:
                    if s[j] != s[i]:
                            rem_k -= 1
                    if rem_k >= 0:
                        c += 1
                    j -= 1

                if c > max_l:
                    max_l = c
                prev = s[i]
        return max_l

    # Complexity O(n) Solution
    def characterReplacement(self, s: str, k: int) -> int:
        # Let's study the cases
        # -Create a dictionary of size k
        #       max_l will be k
        # - While moving to the end:
        #       keep adding elements to the dict to the right 
        # - If no k is available move to the right and remove one from the dictionary



        # Create a diccionary of size k
        rem_k = k
        n = len(s)

        # # variable
        # abc = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
        # char_index = {}
        # for i, l in enumerate(abc):
        #     char_index[l] = i

        # char_list_values = [0]*n
        let_val = {}
        i = 0
        # Fill the dictionary with the k first letters
        while i < n and rem_k > 0:
            if s[i] not in let_val:
                let_val[s[i]] = 1
            else:
                let_val[s[i]] += 1
            k -= 1
            i += 1
        if i == n:
            return n

        total = 0
        i = 1
        while i <= n:
            if s[i-1] not in let_val:
                let_val[s[i-1]] = 1
            else:
                let_val[s[i-1]] += 1
            if k < 0 and i<n and let_val[s[i]] != let_val[s[i-1]]:
                let_val[s[i-1]] -= 1
            elif i<n and let_val[s[i]] != let_val[s[i-1]]:
                k -= 1
            if k + let_val[s[i-1]] > total:
                total = k + let_val[s[i-1]]
            i += 1
        return total

